import React, { useState } from 'react';
import { useCart, useDispatchCart } from '../components/ContextReducer';
import trash from "../components/trash.svg";

export default function Cart() {
  const [checkoutStatus, setCheckoutStatus] = useState(null); // To store the checkout status
  const data = useCart();
  const dispatch = useDispatchCart();

  const handleRemove = (index) => {
    dispatch({ type: "REMOVE", index: index });
  };

  const handleCheckOut = async () => {
    try {
      let userEmail = localStorage.getItem("userEmail");
      let response = await fetch("http://localhost:5000/api/orderData", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          order_data: [data],
          email: userEmail,
          order_date: new Date().toDateString()
        })
      });

      console.log("JSON RESPONSE:::::", response.status);
      
      if (response.status === 200) {
        dispatch({ type: "DROP" });
        setCheckoutStatus("success"); // Set checkout status to success
      } else {
        setCheckoutStatus("failure"); // Set checkout status to failure
      }
    } catch (error) {
      console.error("Error during checkout:", error);
      setCheckoutStatus("failure"); // Set checkout status to failure in case of an error
    }
  };

  let totalPrice = data.reduce((total, food) => total + food.price, 0);

  return (
    <div>
      {checkoutStatus === "success" && (
        <div className="alert alert-success" role="alert">
          Checkout successful!
        </div>
      )}

      {checkoutStatus === "failure" && (
        <div className="alert alert-danger" role="alert">
          Checkout failed. Please try again.
        </div>
      )}

      {data.length === 0 ? (
        <div>
          <div className='m-5 w-100 text-center fs-3' style={{ color: "red" }}>The Cart is Empty!</div>
        </div>
      ) : (
        <div className='container m-auto mt-5 table-responsive  table-responsive-sm table-responsive-md' >
          <table className='table table-hover '>
            <thead className=' text-success fs-4'>
              <tr>
                <th scope='col'>#</th>
                <th scope='col'>Name</th>
                <th scope='col'>Quantity</th>
                <th scope='col'>Option</th>
                <th scope='col'>Amount</th>
                <th scope='col'></th>
              </tr>
            </thead>
            <tbody>
              {data.map((food, index) => (
                <tr key={index}>
                  <th scope='row'>{index + 1}</th>
                  <td>{food.name}</td>
                  <td>{food.qty}</td>
                  <td>{food.size}</td>
                  <td>{food.price}</td>
                  <td><button type="button" className="btn p-0"><img src={trash} alt="delete" onClick={() => handleRemove(index)} /></button> </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div><h1 className='fs-2'>Total Price: {totalPrice}/-</h1></div>
          <div>
            <button className='btn bg-success mt-5 ' onClick={handleCheckOut} > Check Out </button>
          </div>
        </div>
      )}
    </div>
  );
}
