import React, { useState } from 'react';
import { useDispatchCart } from '../components/ContextReducer';
import trash from "../components/trash.svg"

export default function Cart() {
  const [cartData, setCartData] = useState(useCart());
  const dispatch = useDispatchCart();

  if (cartData.length === 0) {
    return (
      <div>
        <div className='m-5 w-100 text-center fs-3' style={{ color: "red" }}>The Cart is Empty!</div>
      </div>
    )
  }

  const handleRemove = (index) => {
    dispatch({ type: "REMOVE", index: index });
    setCartData( useCart()); // Update cartData state after removing an item
  }

  const handleCheckOut = async () => {
    let userEmail = localStorage.getItem("userEmail");

    let response = await fetch("http://localhost:5000/api/orderData", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        order_data: cartData,
        email: userEmail,
        order_date: new Date().toDateString()
      })
    });

    if (response.status === 200) {
      dispatch({ type: "DROP" });
      setCartData([]); // Clear cartData state after checkout
    }
  }

  let totalPrice = cartData.reduce((total, food) => total + food.price, 0);

  return (
    <div>
      <div className='container m-auto mt-5 table-responsive table-responsive-sm table-responsive-md' >
        <table className='table table-hover '>
          <thead className=' text-success fs-4'>
            <tr>
              <th scope='col'>#</th>
              <th scope='col'>Name</th>
              <th scope='col'>Quantity</th>
              <th scope='col'>Option</th>
              <th scope='col'>Amount</th>
              <th scope='col'></th>
            </tr>
          </thead>
          <tbody>
            {cartData.map((food, index) => (
              <tr key={index}>
                <th scope='row'>{index + 1}</th>
                <td>{food.name}</td>
                <td>{food.qty}</td>
                <td>{food.size}</td>
                <td>{food.price}</td>
                <td><button type="button" className="btn p-0"><img src={trash} alt="delete" onClick={() => handleRemove(index)} /></button> </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div><h1 className='fs-2'>Total Price: {totalPrice}/-</h1></div>
        <div>
          <button className='btn bg-success mt-5' onClick={handleCheckOut} > Check Out </button>
        </div>
      </div>
    </div>
  )
}
