import React, { useState } from 'react'
import { useCart, useDispatchCart } from '../components/ContextReducer';
import trash from "../components/trash.svg"
export default function Cart() {
  const [foodOrder, setFoodOrder] = useState({
    userEmail: '',
    items: [
      {
        id: '',
        img: '',
        name: '',
        price: '',
        qty: '',
        size: ''
      },
      // Add more items as needed
    ]
  });

  // Function to handle changes in the food order
  const handleChange = (property, value) => {
    setFoodOrder({
      ...foodOrder,
      [property]: value
    });
  };

  // Function to handle changes in the individual food item
  const handleItemChange = (index, property, value) => {
    const updatedItems = [...foodOrder.items];
    updatedItems[index] = {
      ...updatedItems[index],
      [property]: value
    };
    setFoodOrder({
      ...foodOrder,
      items: updatedItems
    });
  };

  // Example of updating user email
  const handleUserEmailChange = (email) => {
    handleChange('userEmail', email);
  };

  // Example of updating the name of the first item
  const handleItemNameChange = (newName) => {
    handleItemChange(0, 'name', newName);
  };

  let data = useCart();
  let dispatch = useDispatchCart();
  
  if (data.length === 0) {
    return (
      <div>
        <div className='m-5 w-100 text-center fs-3' style={{color:"red"}}>The Cart is Empty!</div>
      </div>
    )
  }
  const handleRemove = (index)=>{
    // console.log(index)
    dispatch({type:"REMOVE",index:index})
  }

  const handleCheckOut = async () => {
    let userEmail = localStorage.getItem("userEmail");
    // console.log(data,localStorage.getItem("userEmail"),new Date())
    let response = await fetch("http://localhost:5000/api/orderData", {
      // credentials: 'include',
      // Origin:"http://localhost:3000/login",
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        order_data: data,
        email: userEmail,
        order_date: new Date().toDateString()
      })
    });
    console.log("JSON RESPONSE:::::", response.status)
    if (response.status === 200) {
      dispatch({ type: "DROP" })
    }
  }

  let totalPrice = data.reduce((total, food) => total + food.price, 0)
  return (
    <div>

      {console.log(data)}
      <div className='container m-auto mt-5 table-responsive  table-responsive-sm table-responsive-md' >
        <table className='table table-hover '>
          <thead className=' text-success fs-4'>
            <tr>
              <th scope='col' >#</th>
              <th scope='col' >Name</th>
              <th scope='col' >Quantity</th>
              <th scope='col' >Option</th>
              <th scope='col' >Amount</th>
              <th scope='col' ></th>
            </tr>
          </thead>
          <tbody>
            {data.map((food, index) => (
              <tr>
                <th scope='row' >{index + 1}</th>
                <td >{food.name}</td>
                <td>{food.qty}</td>
                <td>{food.size}</td>
                <td>{food.price}</td>
                <td ><button type="button" className="btn p-0"><img src={trash} alt="delete" onClick={() => handleRemove(index)} /></button> </td></tr>
            ))}
          </tbody>
        </table>
        <div><h1 className='fs-2'>Total Price: {totalPrice}/-</h1></div>
        <div>
          <button className='btn bg-success mt-5 ' onClick={handleCheckOut} > Check Out </button>
        </div>
      </div>



    </div>
  )
}
